import "core-js/modules/es.array.push.js";
import { fetchHospitalsByParams, fetchUnivs, SetCountry, SetWilaya, getMarkers, setCenter, leave_marker, mark_marker } from "../composable/index";
import { GoogleMap, GMapInfoWindow } from "vue3-google-map";
import GmapCustomMarker from "vue3-gmap-custom-marker";
import { mapMutations } from "vuex";
import Header2 from "./header/MyHeader2.vue";
import MyFooter from "./footer/MyFooter.vue";
import ContactUs from "./MainContant/ContactUs.vue";
import SearchUser from "./MainContant/IsoModal.vue";
import UnivindexComponent from "./newinterface/UnivindexComponent.vue";
export default {
  components: {
    GoogleMap,
    "gmap-custom-marker": GmapCustomMarker,
    GMapInfoWindow,
    Header2,
    ContactUs,
    MyFooter,
    SearchUser,
    UnivindexComponent
  },
  data() {
    return {
      search: false,
      focust: false,
      typeData: "map",
      lat: "",
      long: "",
      nextPage: 1,
      hospitals: [],
      markers: [],
      moreExists: false,
      country: "Turc",
      wilaya: [],
      countries: [{
        word: "Turc",
        fr: "Turquie",
        en: "Turkey",
        ar: "تركيا",
        tu: "Turc",
        lat: 40.945711799603615,
        long: 29.21994356201801,
        flag: "inso-pics/tu.png"
      }],
      dropd1: false,
      dropd2: false,
      destination: {},
      path: [],
      flightPath: null,
      infoWindowPos: null,
      infoContent: null,
      currentMidx: null,
      infoWinOpen: false,
      closeModal: false,
      filter: false
    };
  },
  computed: {
    getCenter: {
      get() {
        return {
          lat: this.$store.state.map.lat,
          lng: this.$store.state.map.long
        };
      },
      set() {
        this.markers.push({
          lat: this.$store.state.map.lat,
          lng: this.$store.state.map.long
        });
      }
    },
    getFlightPath: {
      get() {
        return this.flightPath;
      },
      set(value) {
        this.flightPath = value;
      }
    },
    getMarkers() {
      this.getHospitals;
      getMarkers(this.$store, "univ");
      return this.$store.state.univ.markers;
    },
    getHospitals() {
      return this.$store.state.univ.hospitals;
    },
    getSpecialityName() {
      return this.$store.state.speciality.specialityName;
    },
    getSpecialities() {
      return this.$store.state.speciality.specialities;
    },
    tillOpnned() {
      return this.closeModal;
    }
  },
  methods: {
    hoverMarker(index) {
      mark_marker(index);
    },
    leaveMarker(index) {
      leave_marker(index);
    },
    toggleInfoWindow: function (marker, idx) {
      this.infoWindowPos = marker;
      if (this.infoWindowPos) {
        this.infoWinOpen = !this.infoWinOpen;
        this.currentMidx = idx;
        this.closeModal = false;
      }
    },
    closeit() {
      this.$refs.searchtxt.value = null;
      this.focust = false;
      this.hideSearch();
    },
    showSearch() {
      this.search = true;
    },
    hideSearch() {
      !this.focust ? this.search = false : this.search;
    },
    fct() {
      this.showSearch();
      this.focust = true;
    },
    openSelected: function (index) {
      if (index == this.currentMidx) {
        if (!this.closeModal) {
          return true;
        } else {
          return false;
        }
      }
      return false;
    },
    close: function () {
      this.closeModal = true;
    },
    ...mapMutations(["SET_showModal",
    // map `this.increment()` to `this.$store.commit('increment')`

    // `mapMutations` also supports payloads:
    "SET_typeModal" // map `this.incrementBy(amount)` to `this.$store.commit('incrementBy', amount)`
    ]),

    showInsoModal(e) {
      if (typeof e != "undefined") {
        if (!e.includes('https://') && !e.includes('http://')) {
          e = 'https://' + e;
        }
        window.open(e);
      }
      //this.$store.commit("SET_typeModal", { index, typeModal: "hospital" });
    },

    getCountryWilaya() {
      //this.countries = this.$store.getters.getCountries;
      if (this.country != "country") {
        this.wilaya = this.$store.getters.getCountryById(this.country).wilaya;
      }
    },
    changeMyCountry(key) {
      this.dropd2 = false;
      this.dropd1 = false;
      if (key != "country") {
        this.country = key;
        this.getCountryWilaya();
        SetCountry(this.$store, this.country, this.nextPage);
        const {
          lat,
          long
        } = this.$store.getters.getCountryById(this.country).name;
        setCenter(this.$store, lat, long);
        this.getHospitals;
        this.getMark;
      }
    },
    changeCountry(event) {
      this.dropd2 = false;
      this.dropd1 = false;
      const country = event.target.value;
      this.country = country;
      this.getCountryWilaya();
      SetCountry(this.$store, country, this.nextPage);
      const {
        lat,
        long
      } = this.$store.getters.getCountryById(this.country).name;
      setCenter(this.$store, lat, long);
      this.getHospitals;
      this.getMarkers;
    },
    changeWilaya(event) {
      const wilaya = event.target.value;
      SetWilaya(this.$store, wilaya, this.nextPage);
      fetchUnivs(this.$store, wilaya);
      this.getHospitals;
      this.getMarkers;
      const {
        lat,
        long
      } = this.$store.getters.getWilayaById(this.country, wilaya);
      setCenter(this.$store, lat, long);
    },
    WhatToShow() {
      this.showAll = !this.showAll;
    },
    getRestrictedHospital(speciaName) {
      fetchHospitalsByParams(this.$store, this.nextPage, speciaName);
    },
    handleInput(e) {
      let speciaName = e.target.value;
      fetchHospitalsByParams(this.$store, this.nextPage, speciaName);
    },
    getLocation(closure) {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(position => {
          this.long = position.coords.longitude;
          this.lat = position.coords.latitude;
          this.locationErrorMessage = "";
          setCenter(this.$store, position.coords.latitude, position.coords.longitude);
          /*  localStorage.setItem("storedData", {
              long: position.coords.longitude,
              lat: position.coords.latitude,
            });*/
          closure();
        }, error => {
          if (error.code === 1) {
            this.locationErrorMessage = "Please allow location access.";
          }
        });
      } else {
        console.log("Geolocation is not supported by this browser.");
      }
      /*}*/
    },

    loadMore() {
      try {
        this.nextPage++;
        fetchUnivs(this.$store, this.nextPage, this.long, this.lat, this.getSpecialityName);
        this.moreExists = true;
      } catch (error) {
        this.moreExists = false;
      }
    },
    mark(event) {
      this.destination = {
        lat: event.latLng.lat(),
        lng: event.latLng.lng()
      };
      this.drawDirection();
    },
    drawDirection() {
      this.path = [this.getCenter, this.destination];
      this.filePath = {
        path: [this.getCenter, this.destination],
        geodesic: false,
        strokeColor: "#22c7e5",
        strokeOpacity: 1.0,
        strokeWeight: 2
      };
      this.getFlightPath = this.filePath;
    },
    openInfoWindowTemplate(position) {
      const {
        lat,
        lng,
        address
      } = position;
      this.infoWindow.position = {
        lat: lat,
        lng: lng
      };
      this.infoWindow.template = `<b>${address}</b>`;
      this.infoWindow.isFractionalZoomEnabled = true;
      this.infoWindow.open = true;
    }
  },
  created() {
    setCenter(this.$store, 41.015137, 28.979530);
    this.getCountryWilaya();
    fetchUnivs(this.$store, "istanbul");
  }
};
