import "core-js/modules/es.array.push.js";
/* eslint-disable */
import { fetchAgencyByParams, fetchAgency, SetCountryAgency, SetWilayaAgency, getMarkers, setCenter, mark_marker, leave_marker } from "../composable/index";
import { GoogleMap, GMapInfoWindow } from "vue3-google-map";
import GmapCustomMarker from "vue3-gmap-custom-marker";
import { mapMutations } from "vuex";
import Header2 from "./header/MyHeader2.vue";
import MyFooter from "./footer/MyFooter.vue";
import ContactUs from "./MainContant/ContactUs.vue";
import SearchUser from "./MainContant/IsoModal.vue";
export default {
  components: {
    GoogleMap,
    "gmap-custom-marker": GmapCustomMarker,
    GMapInfoWindow,
    Header2,
    ContactUs,
    MyFooter,
    SearchUser
  },
  data() {
    return {
      search: false,
      focust: false,
      typeData: "map",
      lat: "",
      long: "",
      nextPage: 1,
      hospitals: [],
      markers: [],
      moreExists: false,
      country: "Algerie",
      wilaya: [],
      countries: [{
        fr: "Algerie",
        word: "Algerie",
        en: "Algeria",
        ar: "الجزائر",
        tu: "Cezayir",
        lat: 36.74442239909969,
        long: 3.0726561299136623,
        flag: "inso-pics/dz.png"
      }],
      dropd1: false,
      dropd2: false,
      destination: {},
      path: [],
      flightPath: null,
      infoWindowPos: null,
      infoContent: null,
      currentMidx: null,
      infoWinOpen: false,
      closeModal: false,
      filter: false
    };
  },
  computed: {
    getCenter: {
      get() {
        return {
          lat: this.$store.state.map.lat,
          lng: this.$store.state.map.long
        };
      },
      set() {
        this.markers.push({
          lat: this.$store.state.map.lat,
          lng: this.$store.state.map.long
        });
      }
    },
    getFlightPath: {
      get() {
        return this.flightPath;
      },
      set(value) {
        this.flightPath = value;
      }
    },
    getMarkers() {
      this.getHospitals;
      getMarkers(this.$store, "agency");
      return this.$store.state.agency.markers;
    },
    getHospitals() {
      return this.$store.state.agency.hospitals;
    },
    getSpecialityName() {
      return this.$store.state.speciality.specialityName;
    },
    getSpecialities() {
      return this.$store.state.speciality.specialities;
    },
    tillOpnned() {
      return this.closeModal;
    }
  },
  methods: {
    hoverMarker(index) {
      mark_marker(index);
    },
    leaveMarker(index) {
      leave_marker(index);
    },
    geocodeLatLng(lat, lng) {
      const geocoder = new google.maps.Geocoder();
      const latlng = {
        lat: parseFloat(lat),
        lng: parseFloat(lng)
      };
      return new Promise((resolve, reject) => {
        geocoder.geocode({
          location: latlng
        }, (results, status) => {
          if (status === "OK") {
            if (results[0]) {
              resolve(results[0].formatted_address);
            } else {
              reject("No results found");
            }
          } else {
            reject("Geocoder failed due to: " + status);
          }
        });
      });
    },
    toggleInfoWindow: function (marker, idx) {
      this.infoWindowPos = marker;
      if (this.infoWindowPos) {
        this.infoWinOpen = !this.infoWinOpen;
        this.currentMidx = idx;
        this.closeModal = false;
      }
    },
    openSelected: function (index) {
      if (index == this.currentMidx) {
        if (!this.closeModal) {
          return true;
        } else {
          return false;
        }
      }
      return false;
    },
    close: function () {
      this.closeModal = true;
    },
    closeit() {
      this.$refs.searchtxt.value = null;
      this.focust = false;
      this.hideSearch();
    },
    showSearch() {
      this.search = true;
    },
    hideSearch() {
      !this.focust ? this.search = false : this.search;
    },
    fct() {
      this.showSearch();
      this.focust = true;
    },
    ...mapMutations(["SET_showModal",
    // map `this.increment()` to `this.$store.commit('increment')`

    // `mapMutations` also supports payloads:
    "SET_typeModal" // map `this.incrementBy(amount)` to `this.$store.commit('incrementBy', amount)`
    ]),

    showInsoModal(e) {
      window.open(e);
    },
    getCountryWilaya() {
      //this.countries = this.$store.getters.getCountries;
      if (this.country != "country") {
        this.wilaya = this.$store.getters.getCountryById(this.country).wilaya;
      }
    },
    changeMyCountry(key) {
      this.dropd2 = false;
      this.dropd1 = false;
      if (key != "country") {
        this.country = key;
        this.getCountryWilaya();
        SetCountryAgency(this.$store, this.country, this.nextPage);
        const {
          lat,
          long
        } = this.$store.getters.getCountryById(this.country).name;
        setCenter(this.$store, lat, long);
        this.getHospitals;
        this.getMark;
      }
    },
    changeCountry(event) {
      this.dropd2 = false;
      this.dropd1 = false;
      const country = event.target.value;
      this.country = country;
      this.getCountryWilaya();
      SetCountryAgency(this.$store, country, this.nextPage);
      const {
        lat,
        long
      } = this.$store.getters.getCountryById(this.country).name;
      //setCenter(this.$store, lat, long);
      this.getHospitals;
      this.getMarkers;
    },
    changeWilaya(event) {
      const wilaya = event.target.value;
      SetWilayaAgency(this.$store, wilaya, this.nextPage);
      fetchAgency(this.$store, wilaya);
      this.getHospitals;
      this.getMarkers;
      this.$store.getters.getWilayaById(this.country, wilaya);
      //setCenter(this.$store, lat, long);
    },

    WhatToShow() {
      this.showAll = !this.showAll;
    },
    getRestrictedHospital(speciaName) {
      fetchAgencyByParams(this.$store, this.nextPage, speciaName);
    },
    handleInput(e) {
      let speciaName = e.target.value;
      fetchAgencyByParams(this.$store, this.nextPage, speciaName);
    },
    getLocation(closure) {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(position => {
          this.long = position.coords.longitude;
          this.lat = position.coords.latitude;
          this.locationErrorMessage = "";
          setCenter(this.$store, position.coords.latitude, position.coords.longitude);
          closure();
        }, error => {
          if (error.code === 1) {
            this.locationErrorMessage = "Please allow location access.";
          } else {
            console.log(error);
          }
        });
      } else {
        console.log("Geolocation is not supported by this browser.");
      }
      /*}*/
    },

    loadMore() {
      try {
        this.nextPage++;
        fetchAgency(this.$store, "Alger");
        this.moreExists = true;
      } catch (error) {
        this.moreExists = false;
      }
    },
    mark(event) {
      this.destination = {
        lat: event.latLng.lat(),
        lng: event.latLng.lng()
      };
      this.drawDirection();
    },
    drawDirection() {
      this.path = [this.getCenter, this.destination];
      this.filePath = {
        path: [this.getCenter, this.destination],
        geodesic: false,
        strokeColor: "#22c7e5",
        strokeOpacity: 1.0,
        strokeWeight: 2
      };
      this.getFlightPath = this.filePath;
    },
    openInfoWindowTemplate(position) {
      const {
        lat,
        lng,
        address
      } = position;
      this.infoWindow.position = {
        lat: lat,
        lng: lng
      };
      this.infoWindow.template = `<b>${address}</b>`;
      this.infoWindow.isFractionalZoomEnabled = true;
      this.infoWindow.open = true;
    }
  },
  created() {
    this.getCountryWilaya();
    SetCountryAgency(this.$store, this.country, this.nextPage);
    this.getLocation(() => {
      fetchAgency(this.$store, "", 36.74442239909969, 3.0726561299136623);
    });
    this.getMarkers;
  }
};
