import "core-js/modules/es.array.push.js";
import { fetchHospitalsByParams, fetchHospitals, SetCountry, SetWilaya, getHospitalMarkers, setCenter, mark_marker, leave_marker } from "../../composable/index";
import { GoogleMap, GMapInfoWindow } from "vue3-google-map";
import GmapCustomMarker from "vue3-gmap-custom-marker";
import { mapMutations } from "vuex";
import PhonePart9 from "./PhonePart9.vue";
import CliniqueindexComponent from "../newinterface/CliniqueindexComponent.vue";
export default {
  components: {
    GoogleMap,
    "gmap-custom-marker": GmapCustomMarker,
    GMapInfoWindow,
    PhonePart9,
    CliniqueindexComponent
  },
  data() {
    return {
      typeData: "map",
      mapCenter: {
        lat: null,
        lng: null
      },
      lat: "",
      long: "",
      phone_number: "",
      nextPage: 1,
      hospitals: [],
      markers: [],
      moreExists: false,
      country: "Algerie",
      wilaya: [],
      wila: "Alger",
      countries: [{
        word: "Algerie",
        fr: "Algerie",
        en: "Algeria",
        ar: "الجزائر",
        tu: "Cezayir",
        lat: 36.74442239909969,
        long: 3.0726561299136623,
        flag: "inso-pics/dz.png"
      }, {
        word: "Tunisie",
        fr: "Tunisie",
        en: "Tunisia",
        ar: "تونس",
        tu: "Tunus",
        lat: 33.892166,
        long: 9.561555,
        flag: "inso-pics/tunisie.png"
      }, {
        word: "Turc",
        fr: "Turc",
        en: "Turc",
        ar: "تركيا",
        tu: "Turc",
        lat: 40.945711799603615,
        long: 29.21994356201801,
        flag: "inso-pics/tu.png"
      }],
      dropd1: false,
      dropd2: false,
      destination: {},
      path: [],
      flightPath: null,
      infoWindowPos: null,
      infoContent: null,
      currentMidx: null,
      infoWinOpen: false,
      closeModal: false,
      filter: false,
      showHosMap: true,
      search: false,
      focust: false,
      flag: "dz.png",
      flags: ["dz.png", "tunisie.png", "tu.png"]
    };
  },
  computed: {
    getCenter: {
      get() {
        return {
          lat: this.$store.state.map.lat,
          lng: this.$store.state.map.long
        };
      },
      set() {
        this.markers.push({
          lat: this.$store.state.map.lat,
          lng: this.$store.state.map.long
        });
      }
    },
    getFlightPath: {
      get() {
        return this.flightPath;
      },
      set(value) {
        this.flightPath = value;
      }
    },
    getMarkers() {
      this.getHospitals;
      getHospitalMarkers(this.$store);
      return this.$store.state.catalogue.markers;
    },
    getHospitals() {
      return this.$store.state.catalogue.hospitals;
    },
    getSpecialityName() {
      return this.$store.state.speciality.specialityName;
    },
    getSpecialities() {
      return this.$store.state.speciality.specialities;
    },
    tillOpnned() {
      return this.closeModal;
    }
  },
  methods: {
    toggleInfoWindow: function (marker, idx) {
      this.infoWindowPos = marker;
      if (this.infoWindowPos) {
        this.infoWinOpen = !this.infoWinOpen;
        this.currentMidx = idx;
        this.closeModal = false;
      }
    },
    openSelected: function (index) {
      if (index == this.currentMidx) {
        if (!this.closeModal) {
          return true;
        } else {
          return false;
        }
      }
      return false;
    },
    close: function () {
      this.closeModal = true;
    },
    gotolinks(link, index = 0) {
      if (typeof link === "undefined" || link.length == 0) {
        this.$store.commit("SET_typeModal", {
          index,
          typeModal: "hospital"
        });
      } else {
        if (!link.includes('https://')) {
          link = 'https://' + link;
        }
        window.open(link);
      }
    },
    ...mapMutations(["SET_showModal",
    // map `this.increment()` to `this.$store.commit('increment')`

    // `mapMutations` also supports payloads:
    "SET_typeModal" // map `this.incrementBy(amount)` to `this.$store.commit('incrementBy', amount)`
    ]),

    showInsoModal(index) {
      /* mark_marker(index) */
      this.$store.commit("SET_typeModal", {
        index,
        typeModal: "hospital"
      });
    },
    hoverMarker(index) {
      if (this.showHosMap) {
        mark_marker(index);
      }
    },
    leaveMarker(index) {
      leave_marker(index);
    },
    getCountryWilaya() {
      this.countries = this.$store.getters.getCountries;
      if (this.country != "") {
        this.wilaya = this.$store.getters.getCountryById(this.country).wilaya;
      }
    },
    closeit() {
      this.$refs.searchtxt.value = null;
      this.focust = false;
      this.hideSearch();
    },
    showSearch() {
      this.search = true;
    },
    hideSearch() {
      !this.focust ? this.search = false : this.search;
    },
    fct() {
      this.showSearch();
      this.focust = true;
    },
    changeMyCountry(key) {
      this.dropd2 = false;
      this.dropd1 = false;
      if (key != "") {
        this.country = key;
        this.getCountryWilaya();
        SetCountry(this.$store, this.country, this.nextPage);
        const {
          lat,
          long
        } = this.$store.getters.getCountryById(this.country).name;
        setCenter(this.$store, lat, long);
        this.getHospitals;
        this.getMark;
      }
    },
    changeCountry(event) {
      this.dropd2 = false;
      this.dropd1 = false;
      const country = event.target.value;
      this.country = country;
      this.getCountryWilaya();
      SetCountry(this.$store, country, this.nextPage);
      const {
        lat,
        long
      } = this.$store.getters.getCountryById(this.country).name;
      setCenter(this.$store, lat, long);
      this.getHospitals;
      this.getMarkers;
    },
    changeWilaya(event) {
      const wilaya = event.target.value;
      SetWilaya(this.$store, wilaya, this.nextPage);
      this.getHospitals;
      this.getMarkers;
      const {
        lat,
        long
      } = this.$store.getters.getWilayaById(this.country, wilaya);
      setCenter(this.$store, lat, long);
    },
    WhatToShow() {
      this.showAll = !this.showAll;
    },
    getRestrictedHospital(speciaName) {
      fetchHospitalsByParams(this.$store, this.nextPage, speciaName);
    },
    handleInput(e) {
      let speciaName = e.target.value;
      fetchHospitalsByParams(this.$store, this.nextPage, speciaName);
      this.getHospitals;
      this.getMarkers;
    },
    getLocation(closure) {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(position => {
          this.long = position.coords.longitude;
          this.lat = position.coords.latitude;
          this.mapCenter.lat = position.coords.latitude;
          this.mapCenter.lng = position.coords.longitude;
          this.locationErrorMessage = "";
          setCenter(this.$store, position.coords.latitude, position.coords.longitude);
          /*  localStorage.setItem("storedData", {
              long: position.coords.longitude,
              lat: position.coords.latitude,
            });*/
          closure();
        }, error => {
          if (error.code === 1) {
            this.locationErrorMessage = "Please allow location access.";
          }
        });
      } else {
        console.log("Geolocation is not supported by this browser.");
      }
      /*}*/
    },

    loadMore() {
      try {
        this.nextPage++;
        fetchHospitals(this.$store, this.nextPage, this.long, this.lat, this.getSpecialityName, "", "");
        this.moreExists = true;
      } catch (error) {
        this.moreExists = false;
      }
    },
    mark(event) {
      this.destination = {
        lat: event.latLng.lat(),
        lng: event.latLng.lng()
      };
      this.drawDirection();
    },
    drawDirection() {
      this.path = [this.getCenter, this.destination];
      this.filePath = {
        path: [this.getCenter, this.destination],
        geodesic: false,
        strokeColor: "#22c7e5",
        strokeOpacity: 1.0,
        strokeWeight: 2
      };
      this.getFlightPath = this.filePath;
    },
    openInfoWindowTemplate(position) {
      const {
        lat,
        lng,
        address
      } = position;
      this.infoWindow.position = {
        lat: lat,
        lng: lng
      };
      this.infoWindow.template = `<b>${address}</b>`;
      this.infoWindow.isFractionalZoomEnabled = true;
      this.infoWindow.open = true;
    }
  },
  created() {
    this.getCountryWilaya();
    SetCountry(this.$store, this.country, this.nextPage);
    this.getLocation(() => {
      fetchHospitals(this.$store, this.nextPage, this.long, this.lat, this.getSpecialityName, this.country, "");
    });
  }
};
